import React, { useState } from 'react'
import { Container, Card, Row, Col, Button } from 'react-bootstrap'

const Pizza = ({ pizza }) => {
    const [varient, setVarient] = useState('small')
    const [qunatity, setQuantity] = useState(1)

    return (
        <Container>
            <Card style={{ width: '25rem', margin: '1rem', backgroundColor: "#F1F1F1", }}>
                <Card.Img variant="top" src={pizza.image} />
                <Card.Body style={{ Color: "white" }} >
                    <Card.Title>{pizza.name}</Card.Title>
                    <Card.Text  >
                        <Row>
                            <Col md={6}>
                                <h6>Varients</h6>

                                <select
                                    value={varient}
                                    onChange={e => setVarient(e.target.value)}
                                    style={{ border: "hidden", backgroundColor: "#E7E7E7" }}>
                                    {pizza.varients.map(varient => (
                                        <option>{varient}</option>
                                    )
                                    )}
                                </select>
                            </Col>
                            <Col md={6}>
                                <h6>Qunatity</h6>
                                <select
                                    value={qunatity}
                                    onChange={e => setQuantity(e.target.value)}
                                    style={{ border: "hidden", backgroundColor: "#E7E7E7" }}>
                                    {[...Array(10).keys()].map((v, i) => (
                                        <option value={i + 1}>{i + 1}</option>
                                    ))}
                                </select>
                            </Col>
                        </Row>
                    </Card.Text>
                    <Row>
                        <Col md={6} style={{paddingTop:'1rem'}}><h6>price : {pizza.prices[0][varient] * qunatity} /- ₹</h6></Col>
                        <Col md={6}>
                            <Button className='bg-warning'>Add to Cart</Button>
                        </Col>
                    </Row>
                </Card.Body>
            </Card>
        </Container>
    )
}

export default Pizza
