import React, { useState } from "react";
import { Form, Col, Row, Button } from "react-bootstrap";
import { useDispatch } from "react-redux";
import { filterPizza } from "../actions/pizzaAction";

const Filters = () => {
  const [searchkey, setsearchkey] = useState("");
  const [category, setcategory] = useState("all");
  const dispatch = useDispatch();

  return (
    <div className="p-4 bg-warning mt-4">
      <Form>
        <Row className="mb-3">
          <Form.Label column md={2}>Search Pizza:</Form.Label>
          <Col md={5}>
            <Form.Control
              className="form-control"
              value={searchkey}
              onChange={(e) => setsearchkey(e.target.value)}
              placeholder="Enter pizza name"
            />
          </Col>
        </Row>

        <Row className="mb-3">
          <Form.Label column md={2}>Filter by Category:</Form.Label>
          <Col md={5}>
            <Form.Select
              className="form-select"
              value={category}
              onChange={(e) => setcategory(e.target.value)}
            >
              <option value="all">All</option>
              <option value="veg">Vegetarian</option>
              <option value="nonveg">Non-vegetarian</option>
            </Form.Select>
          </Col>
        </Row>

        <Row className="mb-3">
          <Col md={{ offset: 2 }}>
            <Button
              className="btn btn-primary"
              onClick={() => {
                dispatch(filterPizza(searchkey, category));
              }}
            >
              Search
            </Button>
          </Col>
        </Row>
      </Form>
    </div>
  );
};

export default Filters;
